import React from 'react';
import { useParams } from 'react-router-dom';
import '../styles/PostPage.css';

const posts = [
  { id: 1, title: 'Primer Post', content: 'Este es el contenido de la primera publicaci贸n.' },
  { id: 2, title: 'Segundo Post', content: 'Este es el contenido de la segunda publicaci贸n.' },
];

const PostPage = () => {
  const { id } = useParams();
  const post = posts.find((post) => post.id === parseInt(id));

  if (!post) {
    return <div>Post no encontrado</div>;
  }

  return (
    <div className="post-container">
      <h2>{post.title}</h2>
      <p>{post.content}</p>
    </div>
  );
};

export default PostPage;
