import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/HomePage.css';

const posts = [
  { id: 1, title: 'Primer Post', content: 'Este es el contenido de la primera publicación.' },
  { id: 2, title: 'Segundo Post', content: 'Este es el contenido de la segunda publicación.' },
];

const HomePage = () => {
  return (
    <div className="home-container">
      <h2>Publicaciones del Blog</h2>
      {posts.map((post) => (
        <div key={post.id} className="post-preview">
          <h3>{post.title}</h3>
          <p>{post.content.substring(0, 100)}...</p>
          <Link to={`/post/${post.id}`}>Leer más</Link>
        </div>
      ))}
    </div>
  );
};

export default HomePage;
