import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

const CodesContext = createContext();

export const CodesProvider = ({ children }) => {
  const [codes, setCodes] = useState([]);

  useEffect(() => {
    const fetchCodes = async () => {
      const response = await axios.get('http://localhost:5001/api/codes');
      setCodes(response.data);
    };

    fetchCodes();
  }, []);

  const addCode = async (title, code) => {
    const response = await axios.post('http://localhost:5000/api/codes', { title, code });
    setCodes((prevCodes) => [...prevCodes, response.data]);
  };

  return (
    <CodesContext.Provider value={{ codes, addCode }}>
      {children}
    </CodesContext.Provider>
  );
};

export default CodesContext;
