import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import CodesContext from '../CodesContext';
import '../styles/SubmittedCodesPage.css';

function SubmittedCodesPage() {
  const { codes } = useContext(CodesContext);

  return (
    <div className="submitted-codes-container">
      <h1>Submitted Codes</h1>
      {codes.map((code) => (
        <div key={code.id} className="code-item">
          <h2>Title: {code.title}</h2>
          <pre>{code.code}</pre>
          <Link to={`/view-comments/${code.id}`}>View Comments</Link>
        </div>
      ))}
      <Link to="/blog" className="back-button">Back to Submit</Link>
    </div>
  );
}

export default SubmittedCodesPage;
