import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/BlogPage.css';

function BlogPage() {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí deberías manejar el envío del código, por ahora solo redirige
    navigate('/submitted-codes');
  };

  return (
    <div className="container">
      <div className="title-container">
        <h1>Submit <br /> Your <br /> <span>Code</span></h1>
      </div>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <textarea
            placeholder="Code"
            value={code}
            onChange={(e) => setCode(e.target.value)}
          ></textarea>
          <button type="submit">SUBMIT</button>
        </form>
      </div>
      <div className="code-animation">
        <pre><code>{`const exampleCode = "This is an example of floating code";`}</code></pre>
      </div>
      <div className="code-animation">
        <pre><code>{`console.log("Another line of code moving around");`}</code></pre>
      </div>
      <div className="code-animation">
        <pre><code>{`function displayCode() { return "More floating code"; }`}</code></pre>
      </div>
    </div>
  );
}

export default BlogPage;
