import React, { useState } from 'react';
import styled from 'styled-components';

const BlogPageContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 20px;
  color: #fff;
  background: linear-gradient(135deg, #000, #00ff00);
  background-size: 200% 200%;
  animation: gradientAnimation 10s ease infinite;
  height: 100vh;

  @keyframes gradientAnimation {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }
`;

const Title = styled.h1`
  font-size: 3em;
  color: #00ff00;
  text-shadow: 0 0 5px #00ff00, 0 0 10px #00ff00, 0 0 20px #00ff00, 0 0 30px #00ff00;
  margin-bottom: 20px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  background: rgba(0, 0, 0, 0.7);
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 0 10px #00ff00, 0 0 20px #00ff00, 0 0 30px #00ff00;
  margin-bottom: 20px;
`;

const Input = styled.input`
  margin: 10px 0;
  padding: 10px;
  width: 300px;
  border-radius: 5px;
  border: 2px solid #00ff00;
  background: #0d0d0d;
  color: #fff;
`;

const TextArea = styled.textarea`
  margin: 10px 0;
  padding: 10px;
  width: 300px;
  height: 100px;
  border-radius: 5px;
  border: 2px solid #00ff00;
  background: #0d0d0d;
  color: #fff;
`;

const Button = styled.button`
  background-color: #00ff00;
  color: #000;
  border: none;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 16px;
  border-radius: 5px;
  text-decoration: none;
  box-shadow: 0 0 5px #00ff00, 0 0 10px #00ff00, 0 0 20px #00ff00;
  margin-top: 20px;
  transition: transform 0.3s ease, background-color 0.3s ease;
  margin-bottom: 20px;

  &:hover {
    background-color: #00cc00;
    transform: scale(1.1);
  }
`;

const CodeContainer = styled.div`
  background: rgba(0, 0, 0, 0.7);
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 0 10px #00ff00, 0 0 20px #00ff00, 0 0 30px #00ff00;
  margin-top: 20px;
  width: 80%;
  max-width: 600px;
`;

const CodeTitle = styled.h2`
  color: #00ff00;
  text-shadow: 0 0 5px #00ff00, 0 0 10px #00ff00, 0 0 20px #00ff00, 0 0 30px #00ff00;
`;

const CodeText = styled.pre`
  background: #0d0d0d;
  padding: 10px;
  border-radius: 5px;
  color: #00ff00;
  border: 2px solid #00ff00;
  white-space: pre-wrap;
`;

const BlogPage = () => {
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');
  const [posts, setPosts] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPost = { title, code };
    setPosts([...posts, newPost]);
    setTitle('');
    setCode('');
  };

  return (
    <BlogPageContainer>
      <Title>Submit Your Code</Title>
      <Form onSubmit={handleSubmit}>
        <Input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <TextArea
          placeholder="Code"
          value={code}
          onChange={(e) => setCode(e.target.value)}
        />
        <Button type="submit">Submit</Button>
      </Form>
      {posts.map((post, index) => (
        <CodeContainer key={index}>
          <CodeTitle>{post.title}</CodeTitle>
          <CodeText>{post.code}</CodeText>
          <Button>Edit</Button>
          <Button>Delete</Button>
          <Button>View Comments</Button>
        </CodeContainer>
      ))}
    </BlogPageContainer>
  );
};

export default BlogPage;
