import React, { useState } from 'react';
import './BlogPage.css';
import { useNavigate } from 'react-router-dom';

const BlogPage = () => {
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes agregar la lógica para enviar el código al backend
    navigate('/codes');
  };

  return (
    <div className="container">
      <div className="content">
        <div className="title-container">
          <h1>Submit<br />Your<br /><span>Code</span></h1>
        </div>
        <div className="form-container">
          <form onSubmit={handleSubmit}>
            <input 
              type="text" 
              placeholder="Title" 
              value={title} 
              onChange={(e) => setTitle(e.target.value)} 
            />
            <textarea 
              placeholder="Code" 
              value={code} 
              onChange={(e) => setCode(e.target.value)} 
            ></textarea>
            <button type="submit">SUBMIT</button>
          </form>
        </div>
      </div>
      <div className="code-animation">
        <code>const exampleCode = "This is an example of floating code";</code>
      </div>
      <div className="code-animation">
        <code>console.log("Another line of code moving around");</code>
      </div>
      <div className="code-animation">
        <code>function displayCode() {'{'} return "More floating code"; {'}'}</code>
      </div>
    </div>
  );
};

export default BlogPage;
