import React, { useState } from 'react';
import './BlogPage.css';

const BlogPage = () => {
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <div className="code-container">
        <h1>Your Codes</h1>
        <div className="code-list">
          <div className="code-item">
            <h2>Code Title</h2>
            <button>Edit</button>
            <button>Delete</button>
            <button>View Comments</button>
          </div>
        </div>
        <button onClick={() => setSubmitted(false)}>Back to Submit</button>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="title-container">
        <h1>
          Submit <br /> Your <br /> <span>Code</span>
        </h1>
      </div>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="Title" />
          <textarea placeholder="Code"></textarea>
          <button type="submit">Submit</button>
        </form>
      </div>
      <div className="code-moving">
        <div className="code-line">for (let i = 0; i &lt; 10; i++) { '{' } console.log(i); }</div>
        <div className="code-line">const hello = 'Hello, World!';</div>
        <div className="code-line">function add(a, b) { '{' } return a + b; }</div>
        <div className="code-line">let x = 10;</div>
        <div className="code-line">if (x &gt; 5) { '{' } console.log('x is greater than 5'); }</div>
      </div>
    </div>
  );
};

export default BlogPage;
