import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { CodesContext } from '../CodesContext';
import '../styles/ViewCommentsPage.css';

const ViewCommentsPage = () => {
    const { codeId } = useParams();
    const { codes } = useContext(CodesContext);
    const code = codes.find(c => c.id === codeId);

    if (!code) {
        return <div>Code not found</div>;
    }

    return (
        <div className="view-comments-page">
            <div className="code-container">
                <h2>Title: {code.title}</h2>
                <pre>{code.code}</pre>
                <div className="code-actions">
                    <button>Actualizar</button>
                    <button>Borrar</button>
                    <button>Editar</button>
                </div>
            </div>
            <div className="comments-container">
                <h2>Comments</h2>
                {code.comments.map((comment, index) => (
                    <div key={index} className="comment">
                        <p>{comment}</p>
                        <button>Responder</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ViewCommentsPage;
