import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const CodesContext = createContext();

export const CodesProvider = ({ children }) => {
  const [codes, setCodes] = useState([]);

  useEffect(() => {
    fetchCodes();
  }, []);

  const fetchCodes = async () => {
    try {
      const response = await axios.get('/api/codes');
      setCodes(response.data);
    } catch (error) {
      console.error('Error fetching codes:', error);
    }
  };

  const addCode = async (code) => {
    try {
      const response = await axios.post('/api/codes', code);
      setCodes([...codes, response.data]);
    } catch (error) {
      console.error('Error adding code:', error);
    }
  };

  const deleteCode = async (id) => {
    try {
      await axios.delete(`/api/codes/${id}`);
      setCodes(codes.filter(code => code._id !== id));
    } catch (error) {
      console.error('Error deleting code:', error);
    }
  };

  return (
    <CodesContext.Provider value={{ codes, addCode, deleteCode }}>
      {children}
    </CodesContext.Provider>
  );
};
